import logo from './logo.svg';
import './App.css';
import { Component } from 'react/cjs/react.production.min';

class App extends Component { 

  state = {
    posts: [
      {
        id: 1,
        title: 'O título 1',
        body: 'O corpo 1'
      },
      {
        id: 1,
        title: 'O título 2',
        body: 'O corpo 2'
      },
      {
        id: 1,
        title: 'O título 3',
        body: 'O corpo 3'
      }
    ]
  }


render() { 
  const { posts} = this.state;
 return (
    <div className="App">
       {posts.map(post => <h1>{post.title}</h1>)}
    </div>
  );




}
}
export default App;
