import logo from './logo.svg';
import './App.css';
import { Component } from 'react/cjs/react.production.min';

class App extends Component { 

  state = {
    posts: [
      {
        id: 1,
        title: 'O título 1',
        body: 'O corpo 1'
      },
      {
        id: 2,
        title: 'O título 2',
        body: 'O corpo 2'
      },
      {
        id: 3,
        title: 'O título 3',
        body: 'O corpo 3'
      }
    ]
  }


render() { 
  const { posts } = this.state;
 return (
    <div className="App">
       {posts.map(post => (
         <>
         <h1 key={post.id}>{post.title}</h1>
         <p>{post.body}</p>
         </>
       ))}
    </div>
  );




}
}
export default App;
